text2 = ""

# Counter
# length

def find_frequent_words(sequence, length):
    cnt = Counter()
    position = 0
    while position < (len(sequence) - length):
        nmer = sequence[position:position+length]
        #print(nmer)
        cnt[nmer] += 1
        position += 1




